const wyraz = wyrazy[Math.floor(Math.random() * wyrazy.length)];
const odpowiedzi = [];
let czyTraf = false;
let szanse = Math.floor(Math.random() * 99);
const uzyte = [];
let pozostale = wyraz.length;
document.getElementById('szanse').textContent = szanse;

for (let i = 0; i < wyraz.length; i++){
    odpowiedzi[i] = '_';
}

document.getElementById('gra').textContent = odpowiedzi.join(' ');

document.getElementById('ok').addEventListener('click', function() {
    czyTraf = false;

    document.getElementById('komunikat').textContent = '';
    
    const strzal = document.getElementById('litera').value;
    
    if (strzal.length == 0){
        document.getElementById('komunikat').textContent = 'Dawaj literkę ty geju!';
    }
    else {
        for (let i = 0; i < wyraz.length; i++) {
            if(wyraz[i] == strzal){
                czyTraf = true;
                odpowiedzi[i] = strzal.toLowerCase();
                pozostale--;
                
                document.getElementById('gra').textContent = odpowiedzi.join(' ');
            }
            else {
                if(pozostale == 0) {
                document.getElementById('ok').disabled = true;
                document.getElementById('litera').disabled = true;
                document.getElementById('komunikat').textContent = "UDAŁO CI SIĘ, HASŁO TO: " + wyraz;
            }
            
            }
            
        }
        if (!czyTraf) {
                szanse--;
                document.getElementById('szanse').textContent = szanse;
                if (szanse == 0){
                    document.getElementById('ok').disabled = true;
                    document.getElementById('litera').disabled = true;
                    document.getElementById('komunikat').textContent = "RESETUJ TY NIEPOMYSŁOWY GOŚCIU, HASŁO TO: " + wyraz;
                }
            }
        if (litera === uzyte) {
            document.getElementById('komunikat').textContent = "Daj inną literę"
        }
    }
});
